const SET_FILTER = "SET_FILTER";

let initialState ={
    filterBy : 'all'
};

export default (state= initialState, action) =>{
    switch(action.type) {
        case SET_FILTER:
            return {
                ...state,
                filterBy: action.payload,
            };
        default:
            return state;
    }
}

//action SET_FILTER
export const setFilter = (filter) => ({
    type: SET_FILTER,
    payload: filter
});
